// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

import "../node_modules/@openzeppelin/contracts-upgradeable/proxy/utils/Initializable.sol";
import "./ERC1155Token.sol";

struct LootBox {
  uint256 index;
  uint256 fromId;
  uint256 price;
  address creator;
  uint32 count;
  uint32 supply;
  uint16 creatorBuyingRoyalty;
  uint16 creatorTradingRoyalty;
  address artist;
  uint16 artistBuyingRoyalty;
  uint16 artistTradingRoyalty;
  uint64 release;
  uint16 dropRate;
  uint256[] lootBoxItemsIndex;
}

struct LootBoxItem {
  uint256 index;
  uint256 lootBoxIndex;
  uint256 fromId;
  address artist;
  uint32 count;
  uint32 supply;
  uint16 creatorTradingRoyalty;
  uint16 artistTradingRoyalty;
}

contract Topx is Initializable, ERC1155Token {

  uint256 private randomNounce;
  uint256 private platformTradingRoyalty;
  uint256 private platformTradingUpfrontRoyalty;
  address private treasury;

  mapping(uint256 => LootBox) public lootBoxes;
  mapping(uint256 => LootBoxItem) public lootBoxItems;
  mapping(uint256 => uint256) public tokenIdToLootBoxIndex;
  mapping(uint256 => uint256) public tokenIdToLootBoxItemsIndex;
  mapping(uint256 => uint8) public tokenIdType;

  function initialize(string memory _uri, address _minter, uint256 _platformTradingRoyalty, uint256 _platformTradingUpfrontRoyalty, address _treasury) external initializer {
    __ERC1155Token_init(_uri, _minter);
    platformTradingRoyalty = _platformTradingRoyalty;
    platformTradingUpfrontRoyalty = _platformTradingUpfrontRoyalty;
    treasury = _treasury;
  }

  event EthTopxTransfer(uint8 _action, uint256 _tokenId, uint256 _amount, address _from, address _to);

  event addLootBox(uint256 _packid, uint256 _from, address _creator, uint256 _price, uint256 _supply);
  event addLootBoxItem(uint256 _itemid, uint256 _packid, uint256 _from, address _artist, uint256 _supply);

  function addLootBoxes(LootBox memory _lootBox, LootBoxItem[] memory _lootBoxItems) external {
    require(hasRole(MINTER_ROLE, _msgSender()), "Not a minter");
    LootBox memory lootBox = _lootBox;
    uint256 lootBoxItemsCount = _lootBoxItems.length;
    emit addLootBox(lootBox.index, lootBox.fromId, lootBox.creator, lootBox.price, lootBox.supply);
    for (uint256 i=0; i<lootBoxItemsCount; i++) {
      LootBoxItem memory lootBoxItem = _lootBoxItems[i];
      lootBoxItems[lootBoxItem.index] = lootBoxItem;
      emit addLootBoxItem(lootBoxItem.index, lootBox.index, lootBoxItem.fromId, lootBoxItem.artist, lootBoxItem.supply);
    }
    lootBoxes[lootBox.index] = lootBox;
  }

  event mintLootBox(uint256 _id, address _account, uint256 _tokenId, uint256 _count);

  function buyLootBox(uint256 lootBoxIndex) public payable returns (uint256) {
    LootBox memory lootBox = lootBoxes[lootBoxIndex];
    require(lootBox.index > 0, "Exists");
    require(msg.value >= lootBox.price, "Price");
    require(block.timestamp >= lootBox.release, "Release");
    require(lootBox.count <= lootBox.supply, "Supply");
    uint256 toCreatorRoyalty = mulDiv(msg.value, lootBox.creatorBuyingRoyalty);
    uint256 toArtistRoyalty = mulDiv(msg.value, lootBox.artistBuyingRoyalty);
    uint256 toSeller = msg.value - toCreatorRoyalty - toArtistRoyalty;
    uint256 tokenId = lootBox.fromId + lootBox.count;
    tokenIdToLootBoxIndex[tokenId] = lootBoxIndex;
    tokenIdType[tokenId] = 1;
    lootBoxes[lootBoxIndex].count += 1;
    emit mintLootBox(lootBoxIndex, msg.sender, tokenId, lootBox.count);
    emit EthTopxTransfer(0, tokenId, toSeller, msg.sender, treasury);
    emit EthTopxTransfer(1, tokenId, toCreatorRoyalty, msg.sender, lootBox.creator);
    emit EthTopxTransfer(2, tokenId, toArtistRoyalty, msg.sender, lootBox.artist);
    _mint(msg.sender, tokenId, 1, "");
    payable(lootBox.creator).transfer(toCreatorRoyalty);
    payable(lootBox.artist).transfer(toArtistRoyalty);
    payable(treasury).transfer(toSeller);
    return tokenId;
  }

  event mintItem(uint256 _id, address _account, uint256 _tokenId);

  function openLootBox(uint256 tokenId) public {
    uint256 lootBoxIndex = tokenIdToLootBoxIndex[tokenId];
    LootBox memory lootBox = lootBoxes[lootBoxIndex];
    uint256 lootBoxItemIndexLength = lootBox.lootBoxItemsIndex.length;
    require(lootBox.index > 0, "Exists");
    require(tokenIdType[tokenId] == 1, "Is box");
    _burn(msg.sender, tokenId, 1);
    for (uint256 i=0; i<lootBox.dropRate; i++) {
      uint256 availables = (lootBox.supply - lootBox.count + 1) * lootBox.dropRate;
      uint256 rand = random(availables - i);
      uint256 counter = 0;
      for (uint256 j=0; j<lootBoxItemIndexLength; j++) {
        uint256 currentItem = lootBox.lootBoxItemsIndex[j];
        LootBoxItem memory lootBoxItem = lootBoxItems[currentItem];
        counter = counter + (lootBoxItem.supply - lootBoxItem.count);
        if (rand <= counter) {
          uint256 mintedTokenId = lootBoxItem.fromId + lootBoxItem.count;
          lootBoxItems[currentItem].count++;
          emit mintItem(currentItem, msg.sender, mintedTokenId);
          tokenIdType[mintedTokenId] = 2;
          tokenIdToLootBoxItemsIndex[mintedTokenId] = currentItem;
          _mint(msg.sender, mintedTokenId, 1, "");
          break;
        }
      }
    }
  }

  function buyAndOpenLootBox(uint256 lootBoxIndex) public payable {
    uint256 tokenId = buyLootBox(lootBoxIndex);
    openLootBox(tokenId);
  }

  function getRoyaltyInfo(uint256 _tokenId, uint256 _amount) external view returns (
    address, // creator
    address, // artist
    address, // treasury
    uint256, // creator royalty
    uint256, // artist royalty
    uint256, // platform royalty
    uint256  // platform upfront royalty
  ) {
    uint256 amount = _amount;
    uint256 tokenId = _tokenId;
    if (tokenIdType[tokenId] == 1) {
      LootBox memory lb = lootBoxes[tokenIdToLootBoxIndex[tokenId]];
      return (
        lb.creator,
        lb.artist,
        treasury,
        mulDiv(amount, lb.creatorTradingRoyalty),
        mulDiv(amount, lb.artistTradingRoyalty),
        mulDiv(amount, platformTradingRoyalty),
        mulDiv(amount, platformTradingUpfrontRoyalty)
      );
    } else {
      LootBoxItem memory lbi = lootBoxItems[tokenIdToLootBoxItemsIndex[tokenId]];
      LootBox memory lb = lootBoxes[lbi.lootBoxIndex];
      return (
        lb.creator,
        lbi.artist,
        treasury,
        mulDiv(amount, lbi.creatorTradingRoyalty),
        mulDiv(amount, lbi.artistTradingRoyalty),
        mulDiv(amount, platformTradingRoyalty),
        mulDiv(amount, platformTradingUpfrontRoyalty)
      );
    }
  }

  function mulDiv(uint256 x, uint256 y) private pure returns (uint256) {
    uint256 z = 10000;
    uint256 a = x / z;
    uint256 b = x % z;
    uint256 c = y / z;
    uint256 d = y % z;
    return a * b * z + a * d + b * c + b * d / z;
  }

  function random(uint256 size) private returns (uint256) {
    randomNounce++;
    return uint256(keccak256(abi.encodePacked(block.timestamp, msg.sender, randomNounce))) % size;
  }

  function updatePlatformTradingRoyalty(uint256 _newRoyalty) private {
    require(hasRole(DEFAULT_ADMIN_ROLE, msg.sender), "Not an admin");
    platformTradingRoyalty = _newRoyalty;
  }

  function updatePlatformTradingUpfrontRoyalty(uint256 _newRoyalty) private {
    require(hasRole(DEFAULT_ADMIN_ROLE, msg.sender), "Not an admin");
    platformTradingUpfrontRoyalty = _newRoyalty;
  }

  function getTreasury() external view returns (address) {
    return treasury;
  }
}
